let itemList = [];      // 商品一覧

// 商品一覧をファイルから取得
$(function () {
    $.ajax({
        url: '/json/item.json',
        dataType: 'json'
    })
    .done(function (data) {
        itemList = data;
       // 大分類の生成
        setMainMenu();
    })
    .fail(function () {
        alert("ファイルが読み込めませんでした");
    });
});
